import React from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  ScrollView,
  StatusBar,
} from "react-native";
import { View } from "react-native-web";

import Icon from "react-native-vector-icons/FontAwesome";

let Awal = ["Bandung", "Lampung"];
let Tujuan = ["Bandung", "Lampung", "Surabaya"];
let Tanggal = [11, 12, 13, 14];
let data = [];
data.push(Awal);
data.push(Tujuan);
data.push(Tanggal);

const showbox = () => {
  let box = "";
  for (let i = 0; i < data[0].length; i++) {
    box =
      box +
      (
        <View style={styles.showbox}>
          <Text>(Lokasi Keberangkatan) - (Lokasi Tujuan)</Text>
          <Text>
            <Icon name="user" size={40}></Icon>
            Elang
          </Text>
          <Text>(Tanggal Keberangkatan)</Text>
        </View>
      );
  }

  return box;
};

export const HasilScreen = (navigation) => {
  return (
    <View style={styles.container}>
      <View style={styles.box}></View>
      <View style={styles.box2}>{this.showbox()}</View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 0.8,
  },
  box: {
    backgroundColor: "green",
    flex: 1,
  },
  box2: {
    flex: 3,
  },
  showbox: {
    borderWidth: 1,
    borderRadius: 7,
    padding: 17,
    margin: 20,
  },
});

