import React from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  ScrollView,
  StatusBar,
} from "react-native";
import { View } from "react-native-web";

import Icon from "react-native-vector-icons/FontAwesome";

const App = () => {
  return (
    <View style={styles.container}>
      <View style={styles.box}>
        <Icon
          name="bars"
          size={30}
          style={{ color: "#fff", margin: 20 }}
        ></Icon>
        <Icon
          name="user"
          size={40}
          style={{
            color: "#fff",
            margin: 20,
            transform: [{ scaleX: I18nManager.isRTL ? -1 : 1 }],
          }}
        ></Icon>
      </View>
      <View style={styles.box2} />
      <View style={styles.boxInput}>
        <SafeAreaView>
          <TextInput style={styles.input} />
          <TextInput style={styles.input} />
          <TextInput style={styles.input} />
        </SafeAreaView>
        <Button title="Cari" color="#FF7F50" />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  box: {
    backgroundColor: "green",
    flex: 1.3,
  },
  box2: {
    backgroundColor: "white",
    flex: 1,
  },
  boxInput: {
    backgroundColor: "white",
    position: "absolute",
    top: 200,
    left: 65,
    padding: 20,
    width: 250,
    height: 280,
    borderRadius: 20,
    borderWidth: 1,
  },
  input: {
    height: 40,
    margin: 10,
    borderWidth: 1,
    borderRadius: 3,
    padding: 10,
  },
});

export default App;
