import React from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  ScrollView,
  StatusBar,
} from "react-native";
import { View } from "react-native-web";

import Icon from "react-native-vector-icons/FontAwesome";

let Awal = ["Bandung", "Lampung", "Jawa"];
let Tujuan = ["Bandung", "Lampung", "Surabaya"];
let Tanggal = [11, 12, 13, 14];

const HasilScreen = (navigation) => {
  var box = [];
  for (let i = 0; i < Awal.length; i++) {
    box.push(
      <View style={styles.showbox}>
        <View style={styles.showbox1}>
          <Text
            style={{
              fontSize: 18,
              fontWeight: 600,
              marginRight: 30,
            }}
          >
            {Awal[i]}
          </Text>
          <Text
            style={{
              fontSize: 18,
              fontWeight: 600,
              marginRight: 30,
            }}
          >
            -
          </Text>
          <Text
            style={{
              fontSize: 18,
              fontWeight: 600,
              marginRight: 30,
            }}
          >
            {Tujuan[i]}
          </Text>
        </View>
        <View style={styles.showbox2}>
          <View style={styles.iconshowbox2}>
            <Icon name="plane" size={40} style={{ color: "green" }}></Icon>
            <Text
              style={{
                fontSize: 18,
                fontWeight: 800,
                marginRight: 55,
                marginLeft: 10,
                marginTop: 5,
              }}
            >
              Elang
            </Text>
          </View>
          <Text
            style={{
              fontSize: 18,
              fontWeight: 600,
              marginRight: 50,
              marginTop: 5,
              color: "blue",
            }}
          >
            {Tanggal[i]}
          </Text>
        </View>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.box}>
        <Icon name="user" />
        <Text style={styles.textHeader}>Hiling.id</Text>
        <Text style={styles.textHeader2}>Hasil Pencarian Penerbangan</Text>
        <Text style={styles.textHeader2}>(14 Mei 2022)</Text>
      </View>
      <View style={styles.box2}>{box}</View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 0.8,
  },
  box: {
    backgroundColor: "green",
    flex: 1,
    textAlign: "center",
    padding: 20,
  },
  textHeader: {
    color: "white",
    fontSize: 20,
    fontWeight: 600,
    marginBottom: 10,
  },
  textHeader2: {
    color: "white",
    fontSize: 16,
    fontWeight: 400,
  },
  box2: {
    flex: 3,
  },
  showbox: {
    borderWidth: 1,
    borderRadius: 7,
    padding: 17,
    marginLeft: 20,
    marginRight: 20,
    marginTop: 20,
  },
  showbox1: {
    flexDirection: "row",
    marginBottom: 10,
  },
  showbox2: {
    flexDirection: "row",
  },
  iconshowbox2: {
    flexDirection: "row",
  },
});

export default HasilScreen;
