import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  ScrollView,
  StatusBar,
} from "react-native";
import { View } from "react-native-web";

import Icon from "react-native-vector-icons/FontAwesome";
import { TouchableOpacity } from "react-native-gesture-handler";

const HasilScreen = ({ route, navigation }) => {
  let Data = [
    ["Bandung", "Lampung", "Jawa", "ambon"],
    ["Bandung", "Lampung", "Surabaya", "ambon"],
    [
      "01 Januari 2020",
      "01 Januari 2020",
      "01 Januari 2020",
      "01 Januari 2020",
      "01 Januari 2020",
    ],
  ];

  let box = [];

  for (let i = 0; i < Data[0].length; i++) {
    if (route.params.tgl == Data[2][i]) {
      box.push(
        <View style={styles.showbox}>
          <View style={styles.showbox1}>
            <Text style={styles.textShowbox1}>{Data[0][i]}</Text>
            <View style={styles.iconshowbox2}>
              <Icon name="plane" size={40} style={{ color: "green" }}></Icon>
              <Text style={styles.textBold}>Elang</Text>
            </View>
          </View>
          <View style={styles.showbox3}>
            <Text style={styles.textShowbox1}>-</Text>
          </View>
          <View style={styles.showbox2}>
            <Text style={styles.textShowbox1}>{Data[1][i]}</Text>
            <Text style={styles.textBlue}>{Data[2][i]}</Text>
          </View>
        </View>
      );
    }
  }

  return (
    <View style={styles.container}>
      <View style={styles.box}>
        <TouchableOpacity>
          <Icon
            name="arrow-left"
            size={30}
            style={{
              color: "white",
            }}
          />
        </TouchableOpacity>
        <Icon
          name="user"
          size={40}
          style={{
            marginLeft: "auto",
            marginTop: -30,
            color: "white",
          }}
        />
        <Text style={styles.textHeader}>Hiling.id</Text>
        <Text style={styles.textHeader2}>Hasil Pencarian Penerbangan</Text>
        <Text style={styles.textHeader2}>({route.params.tgl})</Text>
      </View>
      <SafeAreaView style={styles.box2}>
        <ScrollView>{box}</ScrollView>
      </SafeAreaView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 0.8,
  },
  box: {
    backgroundColor: "green",
    flex: 1,

    padding: 20,
  },
  textHeader: {
    textAlign: "center",
    color: "white",
    fontSize: 20,
    fontWeight: 600,
    marginBottom: 10,
  },
  textHeader2: {
    textAlign: "center",
    color: "white",
    fontSize: 16,
    fontWeight: 400,
  },
  box2: {
    flex: 3,
  },
  showbox: {
    borderWidth: 1,
    borderRadius: 7,
    padding: 17,
    marginLeft: 20,
    marginRight: 20,
    marginTop: 20,
    flexDirection: "row",
  },
  showbox1: {
    flexDirection: "column",
    width: 120,
  },
  showbox2: {
    flexDirection: "column",
  },
  showbox3: {
    flexDirection: "column",
  },
  textShowbox1: {
    fontSize: 18,
    fontWeight: 600,
    marginRight: 30,
  },
  iconshowbox2: {
    flexDirection: "row",
  },
  textBold: {
    fontSize: 18,
    fontWeight: 800,
    marginRight: 55,
    marginLeft: 10,
    marginTop: 5,
  },
  textBlue: {
    fontSize: 18,
    fontWeight: 600,
    marginTop: 5,
    color: "blue",
  },
});

export default HasilScreen;
