import React from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  ScrollView,
  StatusBar,
} from "react-native";
import { View } from "react-native-web";

import Icon from "react-native-vector-icons/FontAwesome";

let Awal = ["Bandung", "Lampung", "Jawa"];
let Tujuan = ["Bandung", "Lampung", "Surabaya"];
let Tanggal = [11, 12, 13, 14];

const HasilScreen = (navigation) => {
  var box = [];
  for (let i = 0; i < Awal.length; i++) {
    box.push(
      <View style={styles.showbox}>
        <View style={styles.showbox1}>
          <Text>{Awal[i]}</Text>
          <Text>- {Tujuan[i]}</Text>
        </View>
        <View style={styles.showbox2}>
          <View style={styles.iconshowbox2}>
            <Icon name="plane" size={40}></Icon>
            <Text>Elang</Text>
          </View>
          <Text>{Tanggal[i]}</Text>
        </View>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.box}></View>
      <View style={styles.box2}>{box}</View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 0.8,
  },
  box: {
    backgroundColor: "green",
    flex: 1,
  },
  box2: {
    flex: 3,
  },
  showbox: {
    borderWidth: 1,
    borderRadius: 7,
    padding: 17,
    margin: 20,
  },
  showbox1: {
    flexDirection: "row",
  },
  showbox2: {
    flexDirection: row,
  },
});

export default HasilScreen;
